import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import UserService from '../service/UserService';

function Navbar() {
    // Manage authentication state
    const [isAuthenticated, setIsAuthenticated] = useState(false);
    const [isAdmin, setIsAdmin] = useState(false);

    // Effect to fetch authentication status on component mount
    useEffect(() => {
        const checkAuth = async () => {
            const authStatus = await UserService.isAuthenticated();
            const adminStatus = await UserService.isAdmin();
            setIsAuthenticated(authStatus);
            setIsAdmin(adminStatus);
        };

        checkAuth();
    }, []);

    const handleLogout = async () => {
        const confirmLogout = window.confirm('Are you sure you want to logout this user?');
        if (confirmLogout) {
            await UserService.logout();
            setIsAuthenticated(false);
            setIsAdmin(false); // Reset the state after logout
        }
    };

    return (
        <nav>
            <ul>
                {!isAuthenticated && <li><Link to="/">Phegon Dev</Link></li>}
                {isAuthenticated && <li><Link to="/profile">Profile</Link></li>}
                {isAdmin && <li><Link to="/admin/user-management">User Management</Link></li>}
                {isAuthenticated && <li><Link to="/" onClick={handleLogout}>Logout</Link></li>}
            </ul>
        </nav>
    );
}

export default Navbar;
